delete from cp_pool_source_sub
where pool_id in (
    select id from cp_pool
    where sourceentitlement_id in (
        select id from cp_entitlement
        where pool_id in (
            select p.id from cp_pool p
            where id not in (
                select id from (
                    select s.pool_id
                    from cp_pool_source_sub s
                ) as x
            )
            and type = 'BONUS'
        )
    )
);

